        acceptLanguage: "en-US",
        pragma: "no-cache",
        location: "westus2"
      };
      client.web
        .search(query, options)
        .then(result => {
          console.log("The result is: ");
          console.log(result);
        })
        .catch(err => {
          console.log("An error occurred:");
          console.error(err);
        });
    </script>
  </head>
  <body></body>
</html>

```

