      var baseUri = "<ServiceFabricURL>:<connection-port>";
      var client = new Azure.Servicefabric.ServiceFabricClient({
        baseUri
      });
      client
        .getClusterManifest()
        .then((result) => {
          console.log("The result is:");
          console.log(result);
        })
        .catch((err) => {
          console.log("An error occurred:");
          console.error(err);
        });
    </script>
  </head>
  <body></body>
</html>
```

